pragma solidity ^0.5.0;

contract Adoption {

    address[16] public adopters;
    address private _admin;
    bool public stopped = false;

    modifier stopInEmergency { require(!stopped); _; }

    constructor() public {
        _admin == msg.sender;
    }

    function reset() public {
        require(_admin == msg.sender);
        for (uint i = 0; i < 16; i++) {
            delete adopters[i];
        }
    }

    function isAdmin() public view returns(bool) {
        return _admin == msg.sender;
    }

    function circuitBreaker() public {
        stopped = true;
    }

    // Purchasing a pet
    function purchase(uint petId) public payable stopInEmergency returns (uint) {
        require(petId >= 0 && petId <= 15);
        require (adopters[petId] == address(0x0));
        adopters[petId] = msg.sender;
        // add msg.sender address to customer database
        return petId;
    }

    // Retrieving the adopters
    function getAdopters() public view returns (address[16] memory) {
        return adopters;
    }

}
